const express = require('express');
const router = express.Router();

// Define a GET route with a dynamic parameter `:productId`
router.get('/:productId', (req, res) => {
    const { productId } = req.params;

    if (productId === 'special') {
        res.send(`You've requested the special product with ID: ${productId}`);
    } else {
        res.send(`Product ID: ${productId} requested`);
    }
});

module.exports = router;
